import React, { Component } from 'react'
import './todo.css';

import axios            from 'axios';
import swal             from 'sweetalert';

export default class todo extends Component {
    constructor(props) {
        super(props);
            this.state = {
                title : ""

            }
    }
    handleChange = (event)=>{
        console.log("inside handlechange")
          var name = event.currentTarget.name;
          var value = event.currentTarget.value;
          console.log("value ==>",value)
          this.setState({ [name] : value},()=>{
          });
    }

    handleSubmit = (e) =>{
        e.preventDefault();
        var formValues = {
            title : this.state.title
        }
        console.log("formValues => ",formValues)
    }
    componentDidMount(){
        this.getTodoListData()
    }

    getTodoListData(){
		axios.get("http://localhost:3003/api/todo/get/list")
            .then(response =>{
                console.log("response.data = ",response.data);
                this.setState({
                		todoListData  : response.data.TodoListData,
                });
            })
            .catch(error=>{
                swal("Some Error Occured while getting the live Class Data List",error.message,"error");
        })
    }

    render() {
        return (
            <div className="container PageWrapper">
                <div className="col-12 mt-40 ">
                    <div className="col-6 mx-auto formWrapper">
                        <h2 className="text-center heading"> Create A Task</h2>
                        <hr/>
                        <form className="form-group">
                            <input type="text" placeholder="Enter New Task" name="title" id="title" className="form-control"
                                onChange  = {this.handleChange.bind(this)}
                            />
                            <button type="submit" className="btn btn-primary my-4 float-right" 
                                onClick={this.handleSubmit.bind(this)}
                            ><i class="fa fa-plus" aria-hidden="true"></i>
                            &nbsp;&nbsp; Add task</button>
                        </form>
                    </div>
                </div>
                <div className="col-12 mt40">
                    <div className="col-6 mx-auto tableList">
                        <h2 className="text-center heading"> Task List</h2>
                        <hr/>
                        <table className="table lcListTable">
                        <tbody>
                        {
                            this.state.todoListData && this.state.todoListData.length > 0 
                            ?
                                this.state.todoListData.map((data,index)=>{
                                    return(
                                        <tr key={index}>
                                            <td>
                                                {data.title}
                                            </td>
                                            <td className="float-right">
                                                <a href="" ><i className="fa fa-eye  delcolor"></i></a>
                                            </td>
                                        </tr>
                                    )
                                })
                            : ""
                        }


                      </tbody>

                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
