import React, { Component } from 'react'
import './todo.css';

export default class todo extends Component {
    constructor(props) {
        super(props);
            this.state = {
                addtask : ""

            }
    }
    handleChange = (event)=>{
        console.log("inside handlechange")
          var name = event.currentTarget.name;
          var value = event.currentTarget.value;
          console.log("value ==>",value)
          this.setState({ [name] : value},()=>{
          });
    }

    handleSubmit = (e) =>{
        e.preventDefault();
        var formValues = {
            addtask : this.state.addtask
        }
        console.log("formValues => ",formValues)
    }


    render() {
        return (
            <div className="container">
                <div className="col-12 mt-40">
                <div className="col-6 mx-auto formWrapper">
                    <h2 className="text-center heading"> Create A Task</h2>
                    <hr/>
                    <form className="form-group">
                        <input type="text" placeholder="Enter New Task" name="addtask" id="addtask" className="form-control"
                            onChange  = {this.handleChange.bind(this)}
                        />
                        <button type="submit" className="btn btn-primary my-4 float-right" 
                            onClick={this.handleSubmit.bind(this)}
                        >Add task</button>
                    </form>
                </div>
                </div>
            </div>
        )
    }
}
